mapper.select:
select pre_fix
      ,reg_date
      ,last_number
  from sys_auto_no_dtl
 where pre_fix = #{PRE_FIX}
   and reg_date = #{REG_DATE}

mapper.update:
update sys_auto_no_dtl
   set last_number = LPAD((last_number::int + 1)::text, 4, '0')
 where pre_fix = #{PRE_FIX}
   and reg_date = #{REG_DATE}

mapper.select:
SELECT a.pre_fix || CASE WHEN a.date_type = 'YYYYMMDD' THEN TO_CHAR(to_date(b.reg_date, 'YYYYMMDD'), 'YYYYMMDD') END || b.last_number 
  FROM sys_auto_no a
  JOIN sys_auto_no_dtl b ON a.pre_fix = b.pre_fix
 WHERE a.pre_fix = #{PRE_FIX}
   AND b.reg_date = #{REG_DATE}
